package original;

class SpecialFactorial {
    private static long factorial(int n) {
        long res = 1;
        for (int i = 1; i <= n; i++) {
            res *= i;
        }
        return res;
    }

    public static long specialFactorial(int n) {
        if (n == 0 || n == 1) {
            return 1;
        } else {
            return factorial(n);
        }
    }
