package original;

import org.junit.Test;
import static org.junit.Assert.*;

import java.util.ArrayList;
import java.util.List;

public class FTest {

    @Test
    public void testSum() {
        int n = 5;
        int expected = 15;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }

    @Test
    public void testSumWithZero() {
        int n = 0;
        int expected = 0;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }

    @Test
    public void testSumWithNegativeNumber() {
        int n = -5;
        int expected = 0;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }

    @Test
    public void testSumWithLargeNumber() {
        int n = 1000000;
        int expected = 500000500000;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }

    @Test
    public void testSumWithMaxInteger() {
        int n = Integer.MAX_VALUE;
        int expected = -2147483648;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }

    @Test
    public void testSumWithMinInteger() {
        int n = Integer.MIN_VALUE;
        int expected = 0;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }

    @Test
    public void testSumWithOddNumber() {
        int n = 7;
        int expected = 28;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }

    @Test
    public void testSumWithEvenNumber() {
        int n = 8;
        int expected = 36;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }

    @Test
    public void testSumWithOne() {
        int n = 1;
        int expected = 1;
        int actual = F.sum(n);
        assertEquals(expected, actual);
    }
}
